import React, {Component} from 'react';

class Gallery extends Component{
  state = {
    score: 0,
    highscore: 0
  }

  clickImage = (id) => {
    console.log(id)
    this.setState(
      {
        score: this.state.score +1
      }
    )
  }
  render() {   
  return (
        this.props.images.map(todo => ( 
              <img className='imginsert' key={todo.id} src={todo.title } alt='img' clickImage={this.props.clickImage}/>
         ))

    );
  }
}

export default Gallery;
